import { reqPages } from "./utils/utils.js";

const NORMAL_LOAD = 400;
const AMP = 50;
const MAX_AMP = 100;

const BELOW_NORMAL_LOAD = NORMAL_LOAD - AMP;
const BREAKING = NORMAL_LOAD + AMP;
const BEYOND_BREAKING = NORMAL_LOAD + MAX_AMP;

export let options = {
  stages: [
    { duration: "60s", target: BELOW_NORMAL_LOAD },
    { duration: "60s", target: NORMAL_LOAD },
    { duration: "60s", target: BREAKING },
    { duration: "60s", target: BREAKING },
    { duration: "60s", target: BEYOND_BREAKING },
    { duration: "60s", target: 0 }
  ]
};

export default function() {
  reqPages([
    { url: "/pong", page: "Home" },
    { url: "/boxes", page: "Delivery" }
  ]);
}
